#define FIFO_SIZE 20	
char fifoBuffer[FIFO_SIZE];
int fifoHead = 0;
int fifoTail = 0;
void addToFifo(int value){
	if(((fifoTail + 1) % FIFO_SIZE) == fifoHead){
		return;
	}
	fifoTail = (fifoTail + 1) % FIFO_SIZE;
	fifoBuffer[fifoTail] = value;
}
char removeFromFifo(){
	if(fifoTail == fifoHead){
		return 0;
	}
	char value = fifoBuffer[fifoHead];
	fifoHead = (fifoHead + 1) % FIFO_SIZE;
	return value;
}	
void fifoClear(){
	for(int i = 0; i < FIFO_SIZE; i++){
		fifoBuffer[i] = 0;
	}
	fifoHead = 0;
	fifoTail = 0;
}
	
